mapper.select:
select

from grab_waybill_record
where enabled=1

    and driver_id = #{driverId,jdbcType=INTEGER}


    and status=#{status}


    and waybill_Id=#{waybillId}

mapper.update:
update grab_waybill_record

    status='已抢单'

where enabled=1

    and driver_id = #{driverId,jdbcType=INTEGER}


    and status=#{status}


    and waybill_Id=#{waybillId}

update grab_waybill_record

    status='已拒单'

where enabled=1

    and driver_id = #{driverId,jdbcType=INTEGER}


    and status=#{status}


    and waybill_Id=#{waybillId}

mapper.select:
select
from grab_waybill_record where enabled=1 and status in ('未抢单','已抢单','已结束')
and waybill_Id=#{waybillId}
