mapper.select:
select

from orders
where 1=1

    and order_status = #{orderStatus,jdbcType=INTEGER}


    and create_userId = #{createUserId,jdbcType=INTEGER}


    and start_create_time >= #{startCreateTime}


    and end_create_time <= #{endCreateTime}


    and start_load_time >= #{startLoadTime}


    and end_load_time <= #{endLoadTime}
