mapper.select:
select wa.id, wa.waybill_id, wa.anomaly_type_id, wa.anomaly_desc, wa.processing_status, wa.processing_desc, wa.audit_status, wa.audit_desc, wa.verified_status, wa.adjust_status, wa.create_user_id, wa.create_time, wa.processor_user_id, wa.processor_time, wa.audit_user_id, wa.audit_time, wat.type_name from waybill_anomaly wa inner join waybill_anomaly_type wat on (wa.anomaly_type_id=wat.id) where wa.enabled = 1
and wa.create_user_id = #{createUserId}
and wa.waybill_id = #{waybillId}
and wa.id = #{id}
