mapper.select:
SELECT temp_f.waybill_id, temp_f.customer_id, temp_f.department_id, temp_f.goods_type, temp_f.modify_time, temp_f.waybill_money, temp_f.anomaly_money FROM ( SELECT f.waybill_id AS waybill_id, o.customer_id AS customer_id, w.network_id AS department_id, o.goods_type AS goods_type, w.modify_time AS modify_time, SUM( CASE WHEN f.anomaly_id IS NULL AND f.direction = 1 THEN f.money ELSE 0 END ) AS waybill_money, SUM( CASE
WHEN f.anomaly_id IS NOT NULL AND f.direction = 1 THEN f.money WHEN f.anomaly_id IS NOT NULL AND f.direction = 2 THEN - f.money ELSE 0 END ) AS anomaly_money FROM waybill_customer_fee f LEFT JOIN waybill w ON ( w.id = f.waybill_id ) LEFT JOIN orders o ON ( w.order_id = o.id ) WHERE w.waybill_status = "已完成" AND w.receipt_status = 2 AND f.money > 0
AND o.goods_type = #{goodsType}
AND DATE_FORMAT( w.modify_time, '%Y-%m-%d' ) = DATE_FORMAT( #{endDate}, '%Y-%m-%d' )
and w.network_id in
${did}
and o.customer_id in
${customerId}
GROUP BY f.waybill_id ORDER BY f.create_time DESC ) temp_f WHERE temp_f.waybill_money > 0
