mapper.update:
update waybill set enabled = 0 where id = #{waybillId};
update waybill_items set enabled = 0 where waybill_id = #{waybillId};
update waybill_goods set enabled = 0 where waybill_id = #{waybillId};

mapper.select:
select distinct * from ( select wb.id, wb.order_id, wb.load_site_id, wb.truck_team_contract_id, wb.waybill_status, wb.receipt_status, wb.need_truck_type, wb.truck_id, wb.driver_id, wb.send_time, wb.distance, wb.create_time, wb.created_user_id, wb.modify_time, wb.modify_user_id, wb.enabled, wb.load_time, wb.department_id, wt.create_time trackingDate,wb.notes from waybill wb inner join waybill_tracking wt on (wb.id=wt.waybill_id) where wb.enabled = 1 and wt.enabled = 1 and wb.waybill_status in ('待出发','前往装货地','待装货','待送达','待签收')
and wb.driver_id = #{driverId,jdbcType=INTEGER}
and wt.new_status='待出发' ) tab order by trackingDate desc

select

from waybill
where enabled = 1

    and waybill_status=#{waybillStatus,jdbcType=VARCHAR}


    and driver_id = #{driverId,jdbcType=INTEGER}


    and id = #{id,jdbcType=INTEGER}


    and truck_Id = #{truckId,jdbcType=INTEGER}

order by create_time DESC

select

from waybill
where enabled = 1

    and waybill_status=#{waybillStatus,jdbcType=VARCHAR}


    and driver_id = #{driverId,jdbcType=INTEGER}


    and id = #{id,jdbcType=INTEGER}


    and truck_Id = #{truckId,jdbcType=INTEGER}

order by create_time DESC

select * from waybill where enabled = 1
and driver_id = #{driverId,jdbcType=INTEGER}
and waybill_status in ('前往装货地','待装货','待送达','待签收')

select count(1) from waybill where enabled = 1
and order_id = #{orderId,jdbcType=INTEGER}
and waybill_status = '已拒单'

select * from waybill where enabled = 1
and id = #{id}
and order_id = #{orderId}
AND DATE_FORMAT( create_time, '%Y-%m-%d' ) >= DATE_FORMAT( #{createStartTime}, '%Y-%m-%d' )
AND DATE_FORMAT( create_time, '%Y-%m-%d' ) <= DATE_FORMAT( #{createEndTime}, '%Y-%m-%d' )
and truck_id in
${truckId}
and network_id in
${did}
and order_id in
${orderId}
and waybill_status != '待派单'
and (waybill_status = "待出发" or waybill_status = "前往装货地" or waybill_status = "待装货" or waybill_status = "待送达" or waybill_status = "待签收")
and waybill_status = #{waybillStatus}
and receipt_status in(0,1)
and receipt_status=#{receiptStatus}
order by CASE WHEN waybill_status LIKE '%待派单%' THEN 1 WHEN waybill_status LIKE '%待接单%' THEN 2 WHEN waybill_status LIKE '%待出发%' THEN 3 WHEN waybill_status LIKE '%前往装货地%' THEN 4 WHEN waybill_status LIKE '%待装货%' THEN 5 WHEN waybill_status LIKE '%待送达%' THEN 6 WHEN waybill_status LIKE '%待签收%' THEN 7 WHEN waybill_status LIKE '%已拒单%' THEN 8 WHEN waybill_status LIKE '%已完成%' THEN 9 WHEN waybill_status LIKE '%已取消%' THEN 10 WHEN waybill_status LIKE '%已作废%' THEN 11 END,create_time DESC
order by send_time DESC

mapper.update:
update waybill
set waybill_status='已拒单',modify_time=now(),modify_user_id=1 where waybill_status = '待接单' and id in

    #{waybill.id}

mapper.select:
select

from waybill
where enabled=1 and waybill_status!='已作废' and id = #{id,jdbcType=INTEGER}

mapper.update:
update waybill set waybill_status =#{waybillStatus},
truck_id = #{truckId,jdbcType=INTEGER},
driver_id = #{driverId,jdbcType=INTEGER} where id = #{id};

mapper.select:
select id, order_id, load_site_id, truck_team_contract_id, waybill_status, receipt_status,need_truck_type, truck_id, driver_id, send_time, distance, create_time, created_user_id, modify_time, modify_user_id, enabled,load_time, department_id, network_id from ( select id, order_id, load_site_id, truck_team_contract_id, waybill_status, receipt_status,need_truck_type, truck_id, driver_id, send_time, distance, create_time, created_user_id, modify_time, modify_user_id, enabled,load_time, department_id, network_id from waybill where enabled=1 and waybill_status='待接单'
and waybill_status=#{waybillStatus,jdbcType=VARCHAR}
and truck_Id = #{truckId,jdbcType=INTEGER}
union select w.id, w.order_id, w.load_site_id, w.truck_team_contract_id, w.waybill_status, w.receipt_status,w.need_truck_type, w.truck_id, w.driver_id, w.send_time, w.distance, w.create_time, w.created_user_id, w.modify_time, w.modify_user_id, w.enabled,w.load_time, w.department_id, w.network_id from waybill w inner join grab_waybill_record gwr on w.id=gwr.waybill_id where w.enabled=1 and gwr.enabled=1 and w.waybill_status='待接单' and gwr.status='未抢单'
and gwr.driver_id=#{driverId}
)tab order by create_time DESC

select wb.id waybillId,wb.load_site_id loadSiteId,wb.truck_id truckId,os.customer_id customerId from waybill wb
inner join orders os on (wb.order_id=os.id)

    wb.enabled = 1

        wb.id=#{waybillId}


        wb.goodsType=#{goodsType}
